template:
/* Load defaults from `<userDir>/node_modules/node-red-contrib-uibuilder/front-end/uib-brand.min.css`
 * This version auto-adjusts for light/dark browser settings.
 */
@import url("../uibuilder/uib-brand.min.css");

/* CSS variables to make it easier to control */
:root {
    --container-max-width: 1000px;
    --container-grid-col-gap: 1rem;
    --container-grid-row-gap: 0.5rem;
    --main-width: repeat(12, 1fr);
    /* 1fr; */
    --sidebar-max-width: 1fr;
    --sidebar-min-width: 0.1fr;
}

/* The outer container div */
.container {
    width: 100%;
    max-width: var(--container-max-width);
    margin: 0 auto;
    /* Center the container horizontally */
    display: grid;
    gap: var(--container-grid-row-gap) var(--container-grid-col-gap);

    /* 2 cols x 3 rows layout. Middle row & right column are larger. Left col is constrained min/max */
    grid-template-columns: minmax(var(--sidebar-min-width), var(--sidebar-max-width)) var(--main-width);
    /* grid-template-columns: var(--sidebar-min-width) var(--main-width); */
    grid-template-rows: 0fr 1fr 0fr;
    grid-template-areas:
        "header header"
        "sidebar main"
        "sidebar footer";
    justify-items: stretch;
}

/* Assign the main areas to the grid */
main {
    /* Where you put your content */
    grid-area: main;
    border: 1px dashed silver;
    /* these just for easier understanding */
}

/* Set a child div to have same format as an article */
main > div {
    /* max-width: var(--max-width); */
    border: 1px solid var(--text3);
    border-radius: var(--border-radius);
    padding: var(--border-pad);
    margin: 1rem var(--border-margin);
    background-color: var(--surface3);
}
main > div > h2, main > div > h3, main > div > h4 {
    margin-block-start: 0;
    border-bottom: 1px solid var(--text3);
    padding-block-end: var(--border-pad);
}

header {
    /* Headings, nav, etc */
    grid-area: header;
    border: 1px dashed gold;
}

footer {
    /* at the bottom, (c), dates, etc */
    grid-area: footer;
    border: 1px dashed green;
    margin-top: 0;
}

/* We might want other sidebars so be more explicit for this */
aside.sidebar {
    /* stuff to one side of the main content */
    grid-area: sidebar;
    border: 1px dashed violet;
}

/* Simple horizontal navigation main menu (in the header) */
.nav-main {
    background-color: var(--surface3);
}

.nav-main ul {
    /* Remove bullet points */
    list-style-type: none;
    /* Remove default padding */
    padding: 0;
    /* Remove default margin */
    margin: 0;
    /* Use Flexbox to align items horizontally */
    display: flex;
}

/* Add space between menu items */
.nav-main li {
    margin-right: 1rem;
}

/* Remove margin on the last item */
.nav-main li:last-child {
    margin-right: 0;
}

.nav-main a {
    /* Remove underline from links */
    text-decoration: none;
    /* Add padding for better click area */
    padding: var(--border-pad);
    /* Ensure the entire area is clickable */
    display: block;
}

/* Highlight on hover */
.nav-main a:hover {
    background-color: var(--surface5);
    /* Optional: Add rounded corners */
    border-radius: var(--border-radius);
}

/* Adapt for narrow screens */
@media only screen and (max-width: 512px) {
    /* Very simple adaption example, a single column, 4 rows */
    .container {
        gap: 0.5rem;

        /* 1 cols x 4 rows layout. 2nd row is larger */
        grid-template-columns: 1fr;
        grid-template-rows: 0.1fr 1fr 0.1fr 0.1fr;
        grid-template-areas:
            "header"
            "main"
            "sidebar"
            "footer";
    }
}


/* The rest is not needed if using 
   UIBUILDER v7 since that has an updated
   uib-brand.css that includes this */
/* This lets us use an article like a card display */
article {
    max-width: var(--max-width);
    border: 1px solid var(--text3);
    border-radius: var(--border-radius);
    padding: var(--border-pad);
    margin: 1rem var(--border-margin);
    background-color: var(--surface3);
}
article>h1::before {
    font-size: 50%;
    color: hsl(var(--failure-hue) 100% 50%);
    content: "â›” Do not use H1 headings in articles. "
}
article>h2,
article>h3,
article>h4 {
    margin-block-start: 0;
    border-bottom: 1px solid var(--text3);
    padding-block-end: var(--border-pad);
}

