// @ts-nocheck

/** Example of using the IIFE build of the uibuilder client library
 * Note that uibuilder.start() should no longer be needed.
 * See the documentation if the client doesn't start on its own.
 */
'use strict'

if (!window['jQuery']) {
    uibuilder.log('error', 'index.js', 'jQuery is not available, have you installed it using uibuilder\'s library manager?')
    // jQuery $ not available so use uibuilder's to change the UI
    uibuilder.$('#jqMessage').innerText = 'ERROR: jQuery is not available, have you installed it using uibuilder\'s library manager?'
} else jQuery('#jqMessage').text(`jQuery v${jQuery.fn.jquery} available. uibuilder ${uibuilder.get('version')} available. `)

// logLevel 2+ shows more built-in logging. 0=error,1=warn,2=info,3=log,4=debug,5=trace.
// uibuilder.set('logLevel', 2)
// uibuilder.log('info', 'a prefix', 'some info', {any:'data',life:42})

// NOTE that jQuery's $ function takes precedence over uibuilder's. Also, jQuery === $

// When JQuery is ready
jQuery(function() {
    // ... will run when jQuery is really ready. Probably not needed in most cases ...
})

// Listen for incoming messages from Node-RED
uibuilder.onChange('msg', function(msg) {
    $('#jqMessage').text('Got a message from Node-RED')
    // Dump the msg as text to the "msg" html element
    $('#msg').html(uibuilder.syntaxHighlight(msg))
})

// Send a custom message back to Node-RED using the standard send function
$('#btn1').on('click', function fnSendToNR() {
    uibuilder.send({
        'topic': 'msg-from-uibuilder-front-end',
        'payload': 'A message from the sharp end!',
    })
})

// Send a standard message back to Node-RED using the event send function
// eventSend includes `data-*` attributes, keyb modifiers, etc. Works with any event.
// Note that it needs a DOM event, not a jQuery event.
$('#btn2').on('click', function(jqEvent) {
    uibuilder.eventSend(jqEvent.originalEvent)
})
